document.addEventListener('DOMContentLoaded',function(){function slideDown(elem){elem.style.maxHeight='261px';elem.style.opacity='1'}
function slideUp(elem){elem.style.maxHeight='0'}
document.querySelector('#menu__hamburger').addEventListener('click',function(){if(document.querySelector('#menu').classList===undefined||!document.querySelector('#menu').classList.contains('active')){slideDown(document.querySelector('#menu'));document.querySelector('#menu').classList.add('active')}else{slideUp(document.querySelector('#menu'));document.querySelector('#menu').classList.remove('active')}});window.addEventListener('resize',function(){if(window.innerWidth>=1200){slideDown(document.querySelector('#menu'));document.querySelector('#menu').classList.add('active')}
if(window.innerWidth<1200){slideUp(document.querySelector('#menu'));document.querySelector('#menu').classList.remove('active')}});document.addEventListener('scroll',function(){if(window.innerWidth<=1200)return;if(window.pageYOffset<=100){document.querySelector('.header__bars').classList.remove('sticky')}else{document.querySelector('.header__bars').classList.add('sticky')}});if(window.innerWidth<=1200)return;if(window.pageYOffset<=100){document.querySelector('.header__bars').classList.remove('sticky')}else{document.querySelector('.header__bars').classList.add('sticky')}})